import React from "react";

import {ContactPicker} from '../contactPicker/ContactPicker';

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};


export const AppointmentForm = ({
  contacts,
  name,
  setName,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  function handleChange(e) {
    return setContact(e.target.value);
  }
  

  return (
    <>
      <form onSubmit = {handleSubmit}>
        <label htmlFor ='name'>Name: </label>
        <input type = 'text' id = 'name' value = {name} onChange = {(e) => setName(e.target.value)} />
        <label htmlFor ='date'>Date: </label>
        <input type = 'date' id = 'date' value = {date} onChange = {(e) => setDate(e.target.value)} min = {getTodayString()}/>
        <label htmlFor ='time'>Time: </label>
        <input type = 'time' id = 'time' value = {time} onChange = {(e) => setTime(e.target.value)} />
        <ContactPicker contacts = {contacts} handleChange = {handleChange}/>
        <input type = 'submit' value = 'Submit Appointment' />
      </form>
    </>
  );
};
